        </div>
        
        <div className="todos">
            {
                todos && todos.length> 0 &&
                todos.map((item)=>{
                    return(
                        <div className="todo-list">
                        <h3>{item}</h3>
                        <button onClick={()=>deleteItem(item)}>delete</button>
