import React, { useState } from 'react';

function Header() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="flex flex-col h-full">
      {/* Sidebar */}
      <div className={`fixed top-0 left-0 w-64 bg-gray-800 text-white h-full transform ${isOpen ? "translate-x-0" : "-translate-x-full"} transition-transform duration-300 ease-in-out md:translate-x-0`}>
        <div className="p-4">
          <h1 className="text-xl font-bold">Sidebar</h1>
          {/* Add sidebar content here */}
        </div>
      </div>
      
      {/* Header */}
      <div className="flex items-center justify-between p-4 bg-gray-900 text-white md:hidden">
        <h1 className="text-xl font-bold">Header</h1>
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="focus:outline-none"
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16"></path>
          </svg>
        </button>
      </div>
    </div>
  );
}

export default Header;
