import React from "react";
import { useState } from "react";
import {useN} from "react-router-dom"

const Login =()=>{
    const [email,setEmail] = useState();
    const [password,setPassword] = useState();

    const handleData = async()=>{
        console.log(email, password);
        let data = await fetch("http://localhost:5000/login",{
            method:"post",
            body:JSON.stringify({email,password}),
            headers:{
                "Content-Type":"application/json"
            }
        });
        data = await data.json();
        console.log(data)
        if(data){
            localStorage.setItem("user",JSON.stringify(data))
        }

        
    }
    return(
        <div className="login">
        <h1>Login</h1>
        <input type="text" className="inputBox" placeholder="Enter Email" value={email} onChange={(e)=>setEmail(e.target.value)}/>
        <input type="password" className="inputBox" placeholder="Enter Password" value={password} onChange={(e)=>setPassword(e.target.value)} />
        <button className="appButton" onClick={handleData}>Login</button>
        </div>
    )
}

export default Login;
