import React, { useState } from 'react'
import axios from 'axios';

function Login() {
  const [email,setEmail] = useState();
  const [password, setPassword] = useState();

  const handleData = async()=>{
    let formdata = new FormData();
    formdata.append("email",email)
    formdata.append("password",password);
   
    let response = await axios.post("http://localhost:5000/auth/login", formdata);
    console.log(response.data)
   
    if(response.data.result){
      alert("Please enter correct details!")
    }
    else{
      localStorage.setItem("user", JSON.stringify(response.data));
      navigate("/")
    }
    
      setEmail(""); setPassword("");
  
  }

  return (
    <div className='mt-[8rem] flex flex-col justify-center items-center gap-[1rem] max-w-[30rem] mx-auto'>
      <h2>Login</h2>

      <div className='w-full flex flex-col items-center gap-[1rem]'>

      <input type='text' placeholder='Enter Email' className='w-full py-[0.6rem] px-[1rem] border-solid border-2 border-black rounded' onChange={(e)=>setEmail(e.target.value)} value={email}/>

      <input type='password' placeholder='Enter Password' className='w-full py-[0.6rem] px-[1rem] border-solid border-2 border-black rounded' onChange={(e)=>setPassword(e.target.value)} value={password}/>

      <button className='px-[1.5rem] py-[0.4rem] bg-blue-500 rounded mt-[1rem] text-white' onClick={handleData}>Login</button>
      </div>
    </div>
  )
}

export default Login;
