import React, {useEffect, useState} from 'react'
import { Link, useNavigate } from 'react-router-dom';

function Signup() {
  const [name, setName] = useState();
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  
  const navigate = useNavigate();

  useEffect(()=>{
    let auth = localStorage.getItem("user");
    if(auth){
      navigate("/")
    }
  },[navigate])

  return (
    <div className='mt-[8rem] flex flex-col justify-center gap-[1rem] max-w-[30rem] mx-auto'>
      <h2 className='mb-[1.5rem] font-semibold text-[#000]'>Register</h2>

      <p>
          Please fill in this form to create an account. or
          &nbsp;  <Link className='text-[#FF5252] font-semibold' to="/login">Login</Link>
        </p>

      <div className='w-full flex flex-col items-center gap-[1rem]'>
      <input type='text' placeholder='Enter Name' className='w-full py-[0.6rem] px-[1rem] border-solid border-[1px] border-black rounded'/>

      <input type='text' placeholder='Enter Email' className='w-full py-[0.6rem] px-[1rem] border-solid border-[1px] border-black rounded'/>

      <input type='password' placeholder='Enter Password' className='w-full py-[0.6rem] px-[1rem] border-solid border-[1px] border-black rounded'/>

      <input type='file' name='profile' className='w-full py-[0.6rem] px-[1rem]'/>

      <button className='px-[1.5rem] py-[0.4rem] bg-[#FF5252] rounded mt-[1rem] text-white'>Signup</button>
      </div>
    </div>
  )
}

export default Signup;
