import React from 'react';
import AwesomeSlider from 'react-awesome-slider';
import withAutoplay from 'react-awesome-slider/dist/autoplay';
// import withCaption from 'react-awesome-slider/dist/captioned';
import 'react-awesome-slider/dist/styles.css';
import 'react-awesome-slider/dist/captioned.css'; // Ensure this path is correct

import StartupScreen from './StartupScreen';
import CaptionedStyles from './CaptionedStyles.module.css';



// Create autoplay slider component
const AutoplaySlider = withAutoplay(AwesomeSlider);

// Create captioned slider component
const CaptionedSlider = withCaption(AwesomeSlider);

function Slider() {
  return (
    <>
      {/* Autoplay Slider */}
      <AutoplaySlider
        animation="cubeAnimation"
        play={true}
        cancelOnInteraction={false}
        interval={6000}
      >
        <div data-src="./images/sliderA_01.jpg" />
        <div data-src="./images/sliderA_02.jpg" />
        <div data-src="./images/sliderA_03.jpg" />
      </AutoplaySlider>

      {/* Captioned Slider */}
      <CaptionedSlider
        startupScreen={StartupScreen}
        cssModule={CaptionedStyles}
        screens={[
          {
            backgroundColor: '#4a9c8c',
            media: './images/sliderA_01.jpg',
            caption: 'I want to see what you got.',
          },
          {
            backgroundColor: '#4a9c8c',
            media: './images/sliderA_03.jpg',
            caption: "The answer is -- Don't think about it.",
          },
        ]}
      />
    </>
  );
}

export default Slider;
