import React from 'react';
import AwesomeSlider from 'react-awesome-slider';
import withAutoplay from 'react-awesome-slider/dist/autoplay';
import 'react-awesome-slider/dist/styles.css';
import 'react-awesome-slider/dist/custom-animations/cube-animation.css';
 
const AutoplaySlider = withAutoplay(AwesomeSlider);


import withCaption from 'react-awesome-slider/dist/captioned';
import 'react-awesome-slider/dist/styles.css';
import 'react-awesome-slider/dist/captioned.css';
 
const CaptionedSlider = withCaption(AwesomeSlider);

function Slider() {
  return (
    <>
  <AutoplaySlider animation="cubeAnimation"
    play={true}
    cancelOnInteraction={false}
    interval={6000}
  >
    <div data-src="./images/sliderA_01.jpg" />
    <div data-src="./images/sliderA_02.jpg" />
    <div data-src="./images/sliderA_03.jpg" />
  </AutoplaySlider>

  <CaptionedSlider
    screens={[
      {
        backgroundColor: '#4a9c8c',
        media: '',
        caption: 'I want to see what you got.',
      },
      {
        backgroundColor: '#4a9c8c',
        media: '/images/series/ricknmorty-3.png',
        caption: "The answer is -- Don't think about it.",
      },
    ]}
  />

    </>
  )
}

export default Slider
