import React, { useState } from "react";
import axios from "axios";
import {useNavigate} from "react-router-dom"

const AddPosts = ()=>{
    const [title,setTitle] = useState();
    const [category,setCategory] = useState();
    const [profile,setProfile] = useState();
    const [body,setBody] = useState();
    const [createdAt,setCreatedAt] = useState();

    const navigate = useNavigate();

    const createPosts = async()=>{
        let auth = localStorage.getItem("user");
        let userId = JSON.parse(auth)._id;
       
        const formatDate = (date) => {
            const d = new Date(date);
            let month = '' + (d.getMonth() + 1);
            let day = '' + d.getDate();
            const year = d.getFullYear();
      
            if (month.length < 2) month = '0' + month;
            if (day.length < 2) day = '0' + day;
      
            return [day, month, year].join('/');
          };
      
          let formattedDate = formatDate(createdAt);

        let formdata = new FormData();
        formdata.append("userId",userId);
        formdata.append("title",title);
        formdata.append("category",category)
        formdata.append("profile",profile);
        formdata.append("body",body);
        formdata.append("createdAt",formattedDate);

        const headers = {
            "Authorization": `bearer ${JSON.parse(localStorage.getItem("token"))}`
        }

        let response = await axios.post(`http://localhost:5000/posts/create`,formdata,{headers});

        if(response.data){
            alert("Post Added");
            setTitle(""); setCategory(""); setBody("");
            navigate("/")
        }    
    }

    return(
        <div className="add-posts">
        <h1>Add Post</h1>
        <input type="text" placeholder="Title" className="inputBox" onChange={(e)=> setTitle(e.target.value)} value={title}/>
        <select name="category" className="inputBox" onChange={(e)=> setCategory(e.target.value)} value={category}>
              <option value="select">Select Category</option>
              <option value="discovery">Discovery</option>
              <option value="wildlife">Wild life</option>
              <option value="food">Food</option>
              <option value="science">Science & technology</option>
              <option value="photography">Photography</option>
              <option value="travel">Travel</option>
              <option value="music">Music</option>
              <option value="sports">Sports</option>
              <option value="world">World</option>
            </select>
        <input type="file" name="profile" className="inputBox" onChange={(e)=> setProfile(e.target.files[0])}/>
        <textarea rows={8} cols={3} className="inputBox" placeholder="Body" onChange={(e)=> setBody(e.target.value)} value={body}></textarea>
        <button className="appButton" onClick={createPosts}>Add Post</button>
        </div>
    )
}

export default AddPosts;
