import React, { useState } from "react";
import {useNavigate} from "react-router-dom"

const AddRecipe = ()=>{
    const [title,setTitle] = useState();
    const [category,setCategory] = useState();
    const [summary, setSummary] = useState();
    const [recipePhoto, setRecipePhoto]= useState();
    const [ingradients, setInGradient] = useState([]);
    const [newInGradient, setNewInGradient] = useState("");

    const navigate = useNavigate();

    const handleAddIngredient = ()=>{
      if(newInGradient !== ""){
        setInGradient([...ingradients,newInGradient])
        setNewInGradient("")
      }
    }

   const submitRecipe = ()=>{
    console.log(title, category, summary, recipePhoto, ingradients)
   }


    return(
        <div className="mt-[8rem] w-[100%] sm:w-[90%] mx-auto">
        <h1>Submit Recipe</h1>
       <div className="flex flex-col gap-[2rem] mt-[2rem]">
       <input className="p-[0.8rem] border-solid border-black border-[1px]" type="text" placeholder="Recipe Title" onChange={(e)=> setTitle(e.target.value)} value={title}/>
        
        <select className="p-[0.8rem] border-solid border-black border-[1px]" name="category" onChange={(e)=> setCategory(e.target.value)} value={category}>
              <option value="select">Choose Category</option>
              <option value="discovery">Discovery</option>
              <option value="wildlife">Wild life</option>
              <option value="food">Food</option>
              <option value="science">Science & technology</option>
              <option value="photography">Photography</option>
              <option value="travel">Travel</option>
              <option value="music">Music</option>
              <option value="sports">Sports</option>
              <option value="world">World</option>
            </select>
         
          <textarea className="p-[0.8rem] border-solid border-black border-[1px]" rows={8} cols={3} placeholder="Short Summary" onChange={(e)=> setSummary(e.target.value)} value={summary}></textarea>

          <div className="flex gap-[0.6rem]">
        <label className="font-bold">Upload Recipe photo : </label> 
        <input type="file" name="recipe_photo" onChange={(e)=> setRecipePhoto(e.target.files[0])}/>
        </div>

        <div className="flex gap-[0.6rem]">
        <input type="text" placeholder="Add Ingredient" className="p-[0.8rem] border-solid border-black border-[1px] w-[85%]" onChange={(e)=> setNewInGradient(e.target.value)} value={newInGradient}/>
        <button className="rounded bg-[black] text-white px-[0.8rem] py-[0.5rem] w-fit"
        onClick={handleAddIngredient}>Add New Ingredient</button>
        </div>

        <ul className="flex gap-[0.6rem]">
        {
          ingradients?.map((ingradient,index)=>{
            return<li className="bg-[#d7d7d7] rounded px-[0.5rem] py-[0.5rem]" key={index}>{ingradient}</li>
          })
        }
        </ul>
        <button className="rounded bg-[black] text-white py-[0.5rem] w-fit px-[1rem]" onClick={submitRecipe}>Submit Recipe</button>
        

       </div>
        </div>
    )
}

export default AddRecipe;
