import React from 'react';
import AwesomeSlider from 'react-awesome-slider';
import withAutoplay from 'react-awesome-slider/dist/autoplay';
import 'react-awesome-slider/dist/styles.css';
import 'react-awesome-slider/dist/custom-animations/cube-animation.css';
import './slider.css'; // Ensure this path is correct

const AutoplaySlider = withAutoplay(AwesomeSlider);

function Slider() {
  return (
    <AutoplaySlider
      animation="cubeAnimation"
      play={true}
      cancelOnInteraction={false}
      interval={6000}
    >
      <div className="slider-container" style={{ backgroundImage: 'url("./images/sliderA_01.jpg")' }}>
        <div className="overlay">
          <div className="text">Your Text Here</div>
        </div>
      </div>
      <div className="slider-container" style={{ backgroundImage: 'url("./images/sliderA_02.jpg")' }}>
        <div className="overlay">
          <div className="text">Your Text Here</div>
        </div>
      </div>
      <div className="slider-container" style={{ backgroundImage: 'url("./images/sliderA_03.jpg")' }}>
        <div className="overlay">
          <div className="text">Your Text Here</div>
        </div>
      </div>
    </AutoplaySlider>
  );
}

export default Slider;
