import React, { useState } from "react";
import axios from "axios"
import {useNavigate} from "react-router-dom"

const Signup = ()=>{
    const [name,setName] = useState();
    const [email,setEmail] = useState();
    const [password,setPassword] = useState();
    
    const navigate = useNavigate();

    const handleData= async()=>{
        console.log(name,email,password);
        let data = {
            name:name,
            email:email,
            password:password
        }
        let response = await axios.post('http://localhost:5000/auth/register',data);
        localStorage.setItem("user",JSON.stringify(response.data.user));
        localStorage.setItem("token",JSON.stringify(response.data.auth));
    }

    return(
        <div className="register">
        <h1>Signup</h1>
        <input type="text" placeholder="Enter name" className="inputBox" onChange={(e)=> setName(e.target.value)} value={name}/>
        <input type="text" placeholder="Enter email" className="inputBox" onChange={(e)=> setEmail(e.target.value)} value={email}/>
        <input type="password" placeholder="Enter password" className="inputBox" onChange={(e)=> setPassword(e.target.value)} value={password}/>
        <button className="appButton" onClick={handleData}>Signup</button>
        </div>
    )
}

export default Signup;
