import React, { useEffect } from 'react'
import { useLocation } from "react-router";
import { useDispatch, useSelector } from "react-redux";
import { clickAction } from '../store/slices/itemSlice';

function ProductDetails() {

  const location = useLocation();
  const dispatch = useDispatch();
  const selector = useSelector((state)=> state);
  const clickedItem = selector.items.itemClicked;
  console.log(clickedItem)

  useEffect(()=>{
    let param = new URLSearchParams(location.search);
    let id = param.get("id");
    dispatch(clickAction(id));
  },[])
  return (
    <>
      <section className='marginTop container'>
    <div className='detailContainer'>
    <div className='productImg'>
      <img src={itemClicked?.image} alt='image'/>
    </div>
    <div className='productDetail'>
      <h1>{itemClicked?.title}</h1>
      <h6>${itemClicked?.price}</h6>
      <h3>Description:</h3>
      <p>{itemClicked?.description}</p>
      <div className='productDelivery'>

      </div>
      <h5>Category: {itemClicked?.category}</h5>
      <a className="hero-btn" onClick={()=> itemCartClicked(itemClicked)}>Add to Cart</a>
    </div>
    </div>
    </section>
    </>
  )
}

export default ProductDetails
