import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const PostDetail = () => {
  const params = useParams();
  const [post, setPost] = useState();
  const [comment, setComment] = useState();
  const [likeCount,setlikeCount] = useState(0);

  let auth = localStorage.getItem("user");
  let user = JSON.parse(auth);
  let isLikedByUser;
  let initialCount = 0;


  useEffect(() => {
    getPost();
  }, []);

  const getPost = async () => {
    const headers = {
        "Authorization": `bearer ${JSON.parse(localStorage.getItem("token"))}`,
        "Content-Type":"application/json"
    }

    let response = await axios.get(`http://localhost:5000/posts/getPost/${params.id}`,{headers});
    setPost(response.data);
    console.log("likes", response.data.likes)

    // for(let i=0; i<response.data.likes.length; i++){
    //   if(response.data.likes[i].isLiked === true){
    //     initialCount++;
    //   }
    // }
    // setlikeCount(initialCount)
  };

  const submitComment = async () => {
    let data = {
      userId: user._id,
      commentCreatedAt: Date.now(),
      comment: comment,
    };

    const headers = {
        "Authorization": `bearer ${JSON.parse(localStorage.getItem("token"))}`,
        "Content-Type":"application/json"
    }

    let response = await axios.post(`http://localhost:5000/posts/setComment/${params.id}`,data,{headers});
    setComment("");

    getPost();
  };


  const formatDate = (dateString) => {
    const d = new Date(dateString);
    let month = '' + (d.getMonth() + 1);
    let day = '' + d.getDate();
    const year = d.getFullYear();

    if (month.length < 2) month = '0' + month;
    if (day.length < 2) day = '0' + day;

    return [day, month, year].join('-');
  };


  const postLiked = async(id)=>{
    const headers = {
      "Authorization": `bearer ${JSON.parse(localStorage.getItem("token"))}`,
      "Content-Type":"application/json"
  }

  let response = await axios.get(`http://localhost:5000/posts/getPost/${id}`,{headers});
   
    if (Array.isArray(response.data)){
      response.data.forEach(post => {
        post.likes.forEach(like => {
          if (like.postId === post._id && like.userId === user._id) {
            isLikedByUser = true;
            updateLikes(like.postId, like.userId, like.isLiked);
          }
        });

        if (!isLikedByUser) {
          isLikedByUser = false;
          getLikes(post._id);
        }
      });
    }
  }


const getLikes = async(postId)=>{
  const headers = {
    "Authorization": `bearer ${JSON.parse(localStorage.getItem("token"))}`,
    "Content-Type":"application/json"
}
let data = {
  userId: user._id,
  postId:postId,
  isLiked:true
}
  let result = await axios.post(`http://localhost:5000/posts/likes/add/${postId}`,data,{headers});
  console.log(result)
  getPost();
}

const updateLikes = async(likepostId, likeuserId, isLiked)=>{
  // console.log(likepostId, likeuserId, isLiked)
  const headers = {
    "Authorization": `bearer ${JSON.parse(localStorage.getItem("token"))}`,
    "Content-Type":"application/json"
}

let data = {
  likes:{
    isLiked: !isLiked
  }
}

  let result = await axios.put(`http://localhost:5000/posts/likes/update/${likepostId}/${likeuserId}`,data,{headers});
  console.log(result)
  getPost();
}

  return (
    <>
      <section id="postDetails" className="container">
        {post &&
          post.map((item, index) => {
        
            return (
              <>
                <div className="postDetails" key={index}>
                  <div className="detail-desc">
                    <div className="icons-div">
                      <i className="fas fa-pencil-alt"></i>
                      <h3>{item.userId.name}</h3>
                    </div>
                    <div className="icons-div">
                      <i className="fas fa-clock"></i>
                      <p>{formatDate(item.createdAt)}</p>
                    </div>
                    <div className="icons-div">
                      <i className="far fa-folder-open"></i>
                      <span className="slider-category">{item.category}</span>
                    </div>
                  </div>
                  <h2 className="detail-heading">{item.title}</h2>
                  <img
                    src={`http://localhost:5000/Images/${item.profile}`}
                    alt=""
                    className="detail-img"
                  />

                  <div className="likeComment" id="likeDetail" style={{marginBottom:"1rem"}}>
                    <div className="likeBtn">
                    {
                      item.likes.some((likeObj)=> likeObj.postId === item._id && likeObj.userId === user._id && likeObj.isLiked) ?
                      <svg onClick={()=> postLiked(item._id)} height="25px" width="25px" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink" viewBox="0 0 512 512" xmlSpace="preserve">
      <path style={{ fill: '#FF6647' }} d="M474.655,74.503C449.169,45.72,413.943,29.87,375.467,29.87c-30.225,0-58.5,12.299-81.767,35.566
        c-15.522,15.523-28.33,35.26-37.699,57.931c-9.371-22.671-22.177-42.407-37.699-57.931c-23.267-23.267-51.542-35.566-81.767-35.566
        c-38.477,0-73.702,15.851-99.188,44.634C13.612,101.305,0,137.911,0,174.936c0,44.458,13.452,88.335,39.981,130.418
        c21.009,33.324,50.227,65.585,86.845,95.889c62.046,51.348,123.114,78.995,125.683,80.146c2.203,0.988,4.779,0.988,6.981,0
        c2.57-1.151,63.637-28.798,125.683-80.146c36.618-30.304,65.836-62.565,86.845-95.889C498.548,263.271,512,219.394,512,174.936
        C512,137.911,498.388,101.305,474.655,74.503z"></path>
      <path style={{ fill: '#E35336' }} d="M160.959,401.243c-36.618-30.304-65.836-62.565-86.845-95.889
        c-26.529-42.083-39.981-85.961-39.981-130.418c0-37.025,13.612-73.631,37.345-100.433c21.44-24.213,49.775-39.271,81.138-43.443
        c-5.286-0.786-10.653-1.189-16.082-1.189c-38.477,0-73.702,15.851-99.188,44.634C13.612,101.305,0,137.911,0,174.936
        c0,44.458,13.452,88.335,39.981,130.418c21.009,33.324,50.227,65.585,86.845,95.889c62.046,51.348,123.114,78.995,125.683,80.146
        c2.203,0.988,4.779,0.988,6.981,0c0.689-0.308,5.586-2.524,13.577-6.588C251.254,463.709,206.371,438.825,160.959,401.243z"></path>
    </svg>
                    :
                 
                    <svg onClick={()=> postLiked(item._id)} fill="#FF0000" height="25px" width="25px" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink" viewBox="0 0 471.701 471.701" xmlSpace="preserve">
                        <g>
                          <path d="M433.601,67.001c-24.7-24.7-57.4-38.2-92.3-38.2s-67.7,13.6-92.4,38.3l-12.9,12.9l-13.1-13.1 c-24.7-24.7-57.6-38.4-92.5-38.4c-34.8,0-67.6,13.6-92.2,38.2c-24.7,24.7-38.3,57.5-38.2,92.4c0,34.9,13.7,67.6,38.4,92.3 l187.8,187.8c2.6,2.6,6.1,4,9.5,4c3.4,0,6.9-1.3,9.5-3.9l188.2-187.5c24.7-24.7,38.3-57.5,38.3-92.4 C471.801,124.501,458.301,91.701,433.601,67.001z M414.401,232.701l-178.7,178l-178.3-178.3 c-19.6-19.6-30.4-45.6-30.4-73.3s10.7-53.7,30.3-73.2c19.5-19.5,45.5-30.3,73.1-30.3c27.7,0,53.8,10.8,73.4,30.4l22.6,22.6 c5.3,5.3,13.8,5.3,19.1,0l22.4-22.4c19.6-19.6,45.7-30.4,73.3-30.4c27.6,0,53.6,10.8,73.2,30.3 c19.6,19.6,30.3,45.6,30.3,73.3 C444.801,187.101,434.001,213.101,414.401,232.701z"></path>
                        </g>
                    </svg>
                    }
                   
                    </div>
                  </div>

                  <p className="details-des">{item.body}</p>

                  <div className="likecountdiv">
                    <p className="hashtags">
                      #horizontalimage #image #{item.category}
                    </p>
                    <div className="likecount">
                      <div className="postDComment">
                        <i className="fas fa-comments"></i>
                        <p>{item.comments.length}</p>
                      </div>
                      <div className="postDLike">
                        <i className="fas fa-heart"></i>
                        <p>{likeCount}</p>
                      </div>
                    </div>
                  </div>
                </div>

                <div class="comment">
                  {item.comments &&
                    item.comments.map((comment, index) => {
                      return (
                        <div class="commentsDiv">
                          <div class="post-profile">
                            <div class="profile-img">
                              <img
                                src={`http://localhost:5000/Images/${comment.userId.file}`}
                                alt=""
                              />
                            </div>
                            <div
                              class="profile-desc"
                              style={{ marginTop: "0" }}
                            >
                              <h3>{comment.userId.name}</h3>
                              <p>{formatDate(comment.commentCreatedAt)}</p>
                            </div>
                          </div>
                          <div class="comment-desc">
                            <p>{comment.comment}</p>
                          </div>
                        </div>
                      );
                    })}
                </div>
              </>
            );
          })}
      </section>

      <section id="commentForm" class="container">
        <h2>Leave A Comment</h2>
        <div class="commentForm" id="commentForm">
          <label for="commentTextarea">Your Comment</label>
          <textarea
            name=""
            id="commentTextarea"
            cols="30"
            rows="7"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
          ></textarea>
          <button class="submitBtn" onClick={submitComment}>
            Submit
          </button>
        </div>
      </section>
    </>
  );
};

export default PostDetail;
