import axios from "axios";
import React, { useEffect, useState } from "react";
import {Link, useNavigate} from "react-router-dom";

const Navbar = ()=>{

    const navigate = useNavigate();
    let auth = localStorage.getItem("user");
    let user = JSON.parse(auth);
    const [image,setImage] = useState();

    const deleteItem = ()=>{
        localStorage.clear();
        navigate("/signup")
    }

    useEffect(()=>{
        getImage();
    },[])
    const getImage = async()=>{
        let auth = localStorage.getItem("user");
        let user = JSON.parse(auth);
        if(auth){
        let response = await axios.get(`http://localhost:5000/getImage/${user._id}`);
        setImage(response.data.file)
        }
    }
    console.log("navbarfile",image)

    return(
        <div>
        {
            auth?
            <div className="logo">
            <img src={`http://localhost:5000/Images/${image}`} alt="" className="profile"/>
           <h3> {user.name}</h3>
        </div> :""
        }
        {
            auth? 
            <ul className="nav-ul nav-right">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/signup" onClick={deleteItem}>Logout</Link></li>
        </ul> 
        :
        <ul className="nav-ul nav-right">
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/signup">Signup</Link></li>
        </ul>
        }
      
        </div>
    )
}

export default Navbar;
