import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = ()=>{
    const [email,setEmail] = useState();
    const [password,setPassword] = useState();

    const navigate = useNavigate();

    const handleData = async()=>{
        console.log(email,password);
        let user = await fetch("http://localhost:5000/auth/login",{
            method:"post",
            body:JSON.stringify({email,password}),
            headers:{
                "Content-Type":"application/json"
            }
        }) 
        user = await user.json();
        console.log(user)
        if(user.auth){
            localStorage.setItem("user",JSON.stringify(user.user));
            localStorage.setItem("token",JSON.stringify(user.auth));
            navigate("/")
        }
        else{
         alert("Please Enter Correct Details!")   
        }
       
    }

    return(
        <div className="login">
        <h2>Login</h2>

        <input type="text" placeholder="Enter email" className="inputBox" onChange={(e)=>setEmail(e.target.value)} value={email}/>

        <input type="password" placeholder="Enter password" className="inputBox" onChange={(e)=>setPassword(e.target.value)} value={password}/>

        <button className="appButton" onClick={handleData}>Login</button>
        </div>
    )
}

export default Login;
