import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const PostDetail = () => {
  const params = useParams();
  const [post,setPost] = useState();
  const [comment,setComment] = useState();
  let auth = localStorage.getItem("user");
  let user = JSON.parse(auth);


  useEffect(() => {
    getPost();
  },[]);

  const getPost = async () => {
    let response = await axios.get(
      `http://localhost:5000/posts/getPost/${params.id}`
    );
   setPost(response.data)
  };

  const submitComment = async() =>{
    let data = {
        userId:user._id,
        commentCreatedAt:Date.now(),
        comment:comment
    }
  
    let response = await axios.post(`http://localhost:5000/posts/setComment/${params.id}`,data);
    console.log(response.data)
    setComment("")
  }


  return (
    <>
      <section id="postDetails" className="container">
      {
        post && post.map((item)=>{
            return(
                <div className="postDetails">
          <div className="detail-desc">
            <div className="icons-div">
              <i className="fas fa-pencil-alt"></i>
              <h3>{item.userId.name}</h3>
            </div>
            <div className="icons-div">
              <i className="fas fa-clock"></i>
              <p>{item.createdAt}</p>
            </div>
            <div className="icons-div">
              <i className="far fa-folder-open"></i>
              <span className="slider-category">{item.category}</span>
            </div>
          </div>
          <h2 className="detail-heading">{item.title}</h2>
          <img src={`http://localhost:5000/Images/${item.profile}`} alt="" className="detail-img" />

          <div className="likeComment"></div>

          <p className="details-des">{item.body}</p>

          <div className="likecountdiv">
            <p className="hashtags">#horizontalimage #image #{item.category}</p>
            <div className="likecount">
              <div className="postDComment">
                <i className="fas fa-comments"></i>
                <p>comments length</p>
              </div>
              <div className="postDLike">
                <i className="fas fa-heart"></i>
                <p>like count</p>
              </div>
            </div>
          </div>
        </div>
            )
        })
    }
      </section>

      <div class="commentsDiv">
  <div class="post-profile">
    <div class="profile-img">
        <img src="image/bot_profile.png" alt=""/>
    </div>
    <div class="profile-desc" style="margin-top:0 !important;">
        <h3>Vanita Chawla</h3>
        <p>jan 31, 2024 - 9:00</p>
      </div>
    </div>
    <div class="comment-desc">
      <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Quibusdam eligendi, autem distinctio illo consequatur placeat natus aut dicta ipsa ex.</p>
    </div>
</div>



      <section id="commentForm" class="container">
<h2>Leave A Comment</h2>
<div class="commentForm" id="commentForm"> 
    <label for="commentTextarea">Your Comment</label>
    <textarea name="" id="commentTextarea" cols="30" rows="7" value={comment} onChange={(e)=>setComment(e.target.value)}></textarea>
    <button class="submitBtn" onClick={submitComment}>Submit</button>
</div>

</section>
    </>
  );
};

export default PostDetail;
