import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const PostDetail = () => {
  const params = useParams();
  const [post, setPost] = useState();
  const [comment, setComment] = useState();
  let auth = localStorage.getItem("user");
  let user = JSON.parse(auth);

  console.log(post);

  useEffect(() => {
    getPost();
  }, []);

  const getPost = async () => {
    const headers = {
        "Authorization": `bearer ${JSON.parse(localStorage.getItem("token"))}`,
        "Content-Type":"application/json"
    }

    let response = await axios.get(`http://localhost:5000/posts/getPost/${params.id}`,{headers});
    setPost(response.data);
  };

  const submitComment = async () => {
    let data = {
      userId: user._id,
      commentCreatedAt: Date.now(),
      comment: comment,
    };

    const headers = {
        "Authorization": `bearer ${JSON.parse(localStorage.getItem("token"))}`,
        "Content-Type":"application/json"
    }

    let response = await axios.post(`http://localhost:5000/posts/setComment/${params.id}`,data,{head});
    setComment("");

    getPost();
  };


  return (
    <>
      <section id="postDetails" className="container">
        {post &&
          post.map((item, index) => {
            return (
              <>
                <div className="postDetails" key={index}>
                  <div className="detail-desc">
                    <div className="icons-div">
                      <i className="fas fa-pencil-alt"></i>
                      <h3>{item.userId.name}</h3>
                    </div>
                    <div className="icons-div">
                      <i className="fas fa-clock"></i>
                      <p>{item.createdAt}</p>
                    </div>
                    <div className="icons-div">
                      <i className="far fa-folder-open"></i>
                      <span className="slider-category">{item.category}</span>
                    </div>
                  </div>
                  <h2 className="detail-heading">{item.title}</h2>
                  <img
                    src={`http://localhost:5000/Images/${item.profile}`}
                    alt=""
                    className="detail-img"
                  />

                  <div className="likeComment"></div>

                  <p className="details-des">{item.body}</p>

                  <div className="likecountdiv">
                    <p className="hashtags">
                      #horizontalimage #image #{item.category}
                    </p>
                    <div className="likecount">
                      <div className="postDComment">
                        <i className="fas fa-comments"></i>
                        <p>{item.comments.length}</p>
                      </div>
                      <div className="postDLike">
                        <i className="fas fa-heart"></i>
                        <p>like</p>
                      </div>
                    </div>
                  </div>
                </div>

                <div class="comment">
                  {item.comments &&
                    item.comments.map((comment, index) => {
                      return (
                        <div class="commentsDiv">
                          <div class="post-profile">
                            <div class="profile-img">
                              <img
                                src={`http://localhost:5000/Images/${comment.userId.file}`}
                                alt=""
                              />
                            </div>
                            <div
                              class="profile-desc"
                              style={{ marginTop: "0" }}
                            >
                              <h3>{comment.userId.name}</h3>
                              <p>{comment.commentCreatedAt}</p>
                            </div>
                          </div>
                          <div class="comment-desc">
                            <p>{comment.comment}</p>
                          </div>
                        </div>
                      );
                    })}
                </div>
              </>
            );
          })}
      </section>

      <section id="commentForm" class="container">
        <h2>Leave A Comment</h2>
        <div class="commentForm" id="commentForm">
          <label for="commentTextarea">Your Comment</label>
          <textarea
            name=""
            id="commentTextarea"
            cols="30"
            rows="7"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
          ></textarea>
          <button class="submitBtn" onClick={submitComment}>
            Submit
          </button>
        </div>
      </section>
    </>
  );
};

export default PostDetail;
