import { useElements, useStripe } from "@stripe/react-stripe-js";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";

function Payment() {
  let elements = useElements();
  let stripe = useStripe();
  const [card, setCard] = useState();

  const [searchParams] = useSearchParams();
  const price = searchParams.get("price");
  const interval = searchParams.get("interval");
  const plan = searchParams.get("plan");

  let auth = localStorage.getItem("user");
  let user = JSON.parse(auth);

  const navigate = useNavigate();

  useEffect(() => {
    if (elements && !card) {
      const cardElement = elements.create("card", {
        style: {
          base: {
            iconColor: "#c4f0ff",
            color: "#000",
            fontWeight: "500",
            fontFamily: "Roboto, Open Sans, Segoe UI, sans-serif",
            fontSize: "16px",
            fontSmoothing: "antialiased",
            ":-webkit-autofill": {
              color: "#fce883",
            },
            "::placeholder": {
              color: "#87BBFD",
            },
          },
          invalid: {
            iconColor: "#FF0000",
            color: "#FF0000",
          },
        },
      });
      setCard(cardElement);

      if (cardElement) {
        cardElement.mount(".purchasecard");
      }
    }
  }, [elements, card]);

  const purchaseNow = async () => {
    try {
      const { paymentMethod } = await stripe.createPaymentMethod({
        type: "card",
        card: card,
      });

      console.log("payment", paymentMethod);

      const response = await axios.post("http://localhost:5000/", {
        id: paymentMethod.id,
        amount: price,
        interval: interval,
        plan: plan,
        name: user.name,
        email: user.email,
      });

      console.log("response", response);

      if (response.data) {
        let formdata = new FormData();
        formdata.append("premium", true);

        let response = await axios.post(
          `http://localhost:5000/auth/updateUser/${user._id}`,
          formdata
        );
        console.log("response.data", response.data);
        if (response.data) {
          localStorage.setItem("user", JSON.stringify(response.data.user));
        }
      }

      navigate("/success");
    } catch (error) {
      console.error("Error during purchase:", error.message);
      navigate("/cancel");
    }
  };

  return (
    <div className="mt-[10rem]">
      <div className="w-[95%] md:w-[50%] m-auto flex gap-[1rem] flex-col">
        <div className="bg-[#FFF] rounded-[4px] px-[1rem] py-[2rem] flex gap-[1.5rem] flex-col">
          <p className="text-[1.2rem] font-semibold text-[#674188]">Name : <span className="font-medium">{user.name}</span></p>
          <p className="text-[1.2rem] font-semibold text-[#c4f0ff]">Email : <span className="font-medium">{user.email}</span></p>
        <p className="text-[1.2rem] font-semibold text-[#c4f0ff]">Current Plan:</p>
        <div className="bg-[#c1c8dd] px-[2rem] py-[1rem] flex justify-between items-center">
        <p className="text-[1.2rem] font-semibold text-[#FFF] capitalize">{interval}</p> 
        <button className="bg-[#7795f8] rounded px-[2rem] py-[0.5rem]">${price}</button>
        </div>
        </div>

        <div className="bg-[#FFF] rounded-[4px] px-[1rem] py-[1rem] ">
          <div className="purchasecard"></div>
        </div>

        <button
        className="rounded mt-[2rem] px-[1rem] py-[0.8rem] bg-black text-white"
        onClick={purchaseNow}>pay ${price}</button>

      </div>
    </div>
  );
}

export default Payment;
