import React, { useEffect, useState } from "react";
import {useParams,useNavigate} from "react-router-dom";
import axios from "axios";


const UpdateTask = () => {
    const [title,setTitle] = useState();
    const [content,setContent] = useState();

    const navigate = useNavigate();

    const params = useParams();
    console.log(params.id)

    const handleTask = async()=>{
      let data = {
        title:title,
        content:content
      }
      console.log(data);

      let response = await axios.put(`http://localhost:5000/todo/updatetask/${params.id}`,data);
      console.log(response);
      navigate("/")
    }




  return (
    <div className="register">
      <h1>Update Your Task</h1>
      <input type="text" placeholder="Enter Title" className="inputBox" onChange={(e)=>setTitle(e.target.value)} value={title} />

      <textarea  placeholder="Enter Content" className="inputBox" onChange={(e)=>setContent(e.target.value)} value={content} rows={5} cols={5}></textarea>

      <button onClick={handleTask} className="appButton">Update</button>

    </div>
  );
};

export default UpdateTask;
