import React, { useEffect, useState } from "react";
import {useNavigate} from "react-router-dom"

const Signup = ()=>{
    const [name,setName] = useState();
    const [email,setEmail] = useState();
    const [password,setPassword] = useState();
    const [age,setAge] = useState();
    const [gender,setGender] = useState();
    const [profile,setProfile] = useState();
    
    const navigate = useNavigate();

    useEffect(()=>{
        let auth = localStorage.getItem("user");
        if(auth){
            navigate("/")
        }
    })

    const handleImage=(e)=>{
            const file = e.target.files[0];
            if (file) {
              const reader = new FileReader();
      
              reader.onload = function (e) {
                const img = new Image();
                img.onload = function () {
                  const canvas = document.createElement('canvas');
                  const ctx = canvas.getContext('2d');
      
                  // Set the dimensions you want
                  const maxWidth = 200;
                  const maxHeight = 200;
                  let width = img.width;
                  let height = img.height;
      
                  // Calculate the new dimensions
                  if (width > height) {
                    if (width > maxWidth) {
                      height *= maxWidth / width;
                      width = maxWidth;
                    }
                  } else {
                    if (height > maxHeight) {
                      width *= maxHeight / height;
                      height = maxHeight;
                    }
                  }
      
                  canvas.width = width;
                  canvas.height = height;
                  ctx.drawImage(img, 0, 0, width, height);
      
                  // Store the compressed image
                  const compressedImage = canvas.toDataURL('image/jpeg', 0.7);
      
                  setProfile(compressedImage);

                };
                img.src = e.target.result;
              };
              reader.readAsDataURL(file);
            }   
    }


    const handleData = async(event)=>{
        // event.preventDefault();

        let formdata = new FormData();
        formdata.append("name",name)
        formdata.append("email",email)
        formdata.append("password",password)
        formdata.append("age",age)
        formdata.append("gender",gender)
        formdata.append("profile",profile)

        let user = await fetch("http://localhost:5000/auth/register",{
            method:"post",
            body:formdata,
            headers:{
                "Content-Type":"application/json"
            }
        }) 
        user = await user.json();
        localStorage.setItem("user",JSON.stringify(user.result));
        localStorage.setItem("token",JSON.stringify(user.auth));
        navigate("/")
    }

    return(
        <form action="/uploads" method="post" enctype="multipart/form-data">
        <div className="register">
        <h2>Register</h2>
        <input type="text" placeholder="Enter name" className="inputBox" onChange={(e)=>setName(e.target.value)} value={name}/>

        <input type="text" placeholder="Enter email" className="inputBox" onChange={(e)=>setEmail(e.target.value)} value={email}/>

        <input type="password" placeholder="Enter password" className="inputBox" onChange={(e)=>setPassword(e.target.value)} value={password}/>

        <input type="number" placeholder="Enter age" className="inputBox" onChange={(e)=>setAge(e.target.value)} value={age}/>

        <input type="text" placeholder="Enter gender" className="inputBox" onChange={(e)=>setGender(e.target.value)} value={gender}/>

        <input type="file" name='profile' accept="image/*" onChange={handleImage}/>

        <button className="appButton" onClick={handleData}>Signup</button>

        </div>
        </form>
    )
}

export default Signup;
