import React, { useEffect, useState } from "react";
import axios from "axios"
import {useNavigate} from "react-router-dom";

const Signup = ()=>{
    const [name,setName] = useState();
    const [email,setEmail] = useState();
    const [password,setPassword] = useState();
    const [file,setFile] = useState();

    const navigate = useNavigate();

    useEffect(()=>{
        let auth = localStorage.getItem("user");
        if(auth){
            navigate("/")
        }
    },[navigate])

    const handleData= async()=>{
        let formdata = new FormData();
        formdata.append("name",name)
        formdata.append("email",email)
        formdata.append("password",password);
        formdata.append("file",file);
       
        let response = await axios.post('http://localhost:5000/auth/register',formdata);
        localStorage.setItem("user",JSON.stringify(response.data.user));
        localStorage.setItem("token",JSON.stringify(response.data.auth));
        navigate("/")
    }

    return(
        <div className="register">
        <h1>Signup</h1>
        <input type="text" placeholder="Enter name" className="inputBox" onChange={(e)=> setName(e.target.value)} value={name}/>

        <input type="text" placeholder="Enter email" className="inputBox" onChange={(e)=> setEmail(e.target.value)} value={email}/>

        <input type="password" placeholder="Enter password" className="inputBox" onChange={(e)=> setPassword(e.target.value)} value={password}/>

        <input type="file" name="file" onChange={(e)=> setFile(e.target.files[0])} className="inputBox"/>
        
        <button className="appButton" onClick={handleData}>Signup</button>
        </div>
    )
}

export default Signup;
