import React, { useState } from "react";

function Counter(){
    let counter = 0;
    const [count, setCount] = useState(counter);

    // function incrementFive(){
    //     setCount(count + 5);
    // }


    // Not working correctly it adds by 1
    // function incrementFive(){
    //     for(let i=0; i<5; i++){
    //         setCount(count + 1);
    //     }
    // }


    // Use state with previous value
    function incrementFive(){
        for(let i=0; i<5; i++){
           setCount(prevValue => prevValue+1);
        }
    }

    return(
        <>
        <div>
            <h1>Counter</h1>
            <h3>{count}</h3>
            <br/>
         
            <div className="counter">
                <button onClick={()=>setCount(prevValue => prevValue+1)}>Increment</button>
                <button onClick={()=>setCount(counter)}>Reset</button>
                <button onClick={()=>setCount(count-1)}>Decrement</button>
                <button onClick={()=>incrementFive()}>Increment with 5</button>
            </div>
            </div>
        </>
    )
}

export default Counter;
