import React, { useEffect, useState } from "react";
import {useParams} from "react-router-dom"

const UpdateProduct =()=>{
    const [name,setName] = useState();
    const [price,setPrice] = useState();
    const [category,setCategory] = useState();
    const [company,setCompany] = useState();

    const params = useParams();
   
    useEffect(()=>{
        getData();
    })

    const getData=async()=>{
        let id = params.id;
        let result = await fetch(`http://localhost:5000/products/${id}`);
        result = await result.json();
        setName(result.name);
        setPrice(result.price);
        setCategory(result.category);
        setCompany(result.company);
    }

    const updateProduct=async()=>{
        console.log(name,price,category,company);
        let id = params.id;
        let result = await fetch(`http://localhost:5000/products/${id}`,{
            method:"put"
        });
        result = await result.json();
        
    }

    

    return(
        <div className="add-product">
        <h1>Update Product</h1>
        <input type="text" placeholder="Enter Name" className="inputBox" onChange={(e)=>setName(e.target.value)} value={name}/>
      
        <input type="text" placeholder="Enter Price" className="inputBox" onChange={(e)=>setPrice(e.target.value)} value={price}/>
        
        <input type="text" placeholder="Enter Category" className="inputBox" onChange={(e)=>setCategory(e.target.value)} value={category}/>
       
        <input type="text" placeholder="Enter Company" className="inputBox" onChange={(e)=>setCompany(e.target.value)} value={company}/>
        
        <button className="appButton" onClick={updateProduct}>Update Product</button>
        
        </div>
    )
}

export default UpdateProduct;
