import React, { useState } from "react";

const AddProduct =()=>{
    const [name,setName] = useState();
    const [price,setPrice] = useState();
    const [category,setCategory] = useState();
    const [company,setCompany] = useState();
    const [error,setError] = useState(false);

    const handle401 = () => {
        localStorage.removeItem("user");
        localStorage.removeItem("token");
        navigate("/login");
      };

    const addProduct = async()=>{
        console.log(!name)
        if(!name || !price || !category || !company){
            setError(true);
            return false;
        }
        console.log(name,price,category,company);
        let userId = JSON.parse(localStorage.getItem("user"))._id;
        let result = await fetch("http://localhost:5000/add-product",{
            method:"post",
            body:JSON.stringify({name,price,category,userId,company}),
            headers:{
                "Content-Type":"application/json",
                authorization:`bearer ${JSON.parse(localStorage.getItem("token"))}`
            }
        })
        result = await result.json();
        console.log(result);
        setName(""); setPrice(""); setCategory(""); setCompany("")
        if(result.status === 401){
            handle401();
          }
    }

    return(
        <div className="add-product">
        <h1>Add Product</h1>
        <input type="text" placeholder="Enter Name" className="inputBox" onChange={(e)=>setName(e.target.value)} value={name}/>
      {error && !name && <span className="invalid-input">Enter a valid name</span>}
        
        <input type="text" placeholder="Enter Price" className="inputBox" onChange={(e)=>setPrice(e.target.value)} value={price}/>
        {error && !price && <span className="invalid-input">Enter a valid price</span>}
       
        <input type="text" placeholder="Enter Category" className="inputBox" onChange={(e)=>setCategory(e.target.value)} value={category}/>
        {error && !category && <span className="invalid-input">Enter a valid category</span>}
        
        <input type="text" placeholder="Enter Company" className="inputBox" onChange={(e)=>setCompany(e.target.value)} value={company}/>
        {error && !company && <span className="invalid-input">Enter a valid company</span>}
        
        <button className="appButton" onClick={addProduct}>Add Product</button>
        
        </div>
    )
}

export default AddProduct;
