import React, { useEffect, useState } from 'react';
import axios from "axios";

function Recipes() {
  const [recipes, setRecipes] = useState();

  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  useEffect(()=>{
    getRecipes();
  },[]);

  const getRecipes= async()=>{
    let response = await axios.get("http://localhost:5000/recipe/getAllRecipes");
    setRecipes(response.data);
  }


  return (
    <div className='mt-[5rem] w-[95%] sm:w-[80%] mx-auto'>
      <h3>Latest Recipes</h3>
      <div className='grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-[1.5rem] mt-[2rem]'>
        {
            recipes?.map((recipe,index)=>{
              return(
                <>
                  <div onClick={openModal} key={index} className='cursor-pointer flex justify-center flex-col gap-[0.6rem] w-[250px] '>
                    <img className='w-[250px] h-[250px]' src={recipe.recipe_photo} alt=''/>
                    <p className='text-[#606060] font-medium text-[1.1rem]'>{recipe.recipe_title}</p>
                    <p className='self-end text-[#909090] font-bold flex gap-[0.3rem] items-center'><i class="fas fa-clock"></i>{recipe.prep_time}</p>
                  </div>
                </>
              )
            })
        }

        {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-8 rounded-lg shadow-lg relative w-1/2">
            <button
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
              onClick={closeModal}
            >
              &times;
            </button>
            <h2 className="text-2xl font-bold mb-4">Subscription Plans</h2>
            <p className="mb-4">Choose a plan that fits your needs:</p>
            <ul className="list-disc list-inside mb-4">
              <li>Plan A: $10/month</li>
              <li>Plan B: $20/month</li>
              <li>Plan C: $30/month</li>
            </ul>
            <button
              className="bg-green-500 text-white px-6 py-3 rounded-md shadow-md hover:bg-green-600 transition-all"
              onClick={closeModal}
            >
              Close
            </button>
          </div>
        </div>
      )}
      </div>
    </div>
  )
}

export default Recipes
