// Component within component but we can use only that component not outside it and there are twe ways.
import React from "react";
import User from "./Components/Classuser";
const App = () =>{
    function Other(){
        return(
            <><h1>Vanita Chawla</h1></>
        )
    }
    return (
        <>
        <User/>
        <Other/>
        {Other()}
        </>
    )
}


// Can we write the code without jsx and just using js. yes :)


export default App;
