import React,{useEffect} from 'react'
import {Link, useNavigate} from "react-router-dom";

function Login() {
  
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  
  const navigate = useNavigate();

  useEffect(()=>{
    let auth = localStorage.getItem("user");
    if(auth){
      navigate("/")
    }
  },[navigate])

  return (
    <div className='mt-[8rem] flex flex-col justify-center gap-[1rem] max-w-[30rem] mx-auto'>
      <h2 className='mb-[1.5rem] font-semibold text-[#000]'>Login</h2>

      <p>
          Already have an account? Login in or 
          &nbsp; <Link className='text-[#FF5252] font-semibold' to="/signup">Sign Up</Link>
        </p>

      <div className='w-full flex flex-col items-center gap-[1rem]'>

      <input type='text' placeholder='Enter Email' className='w-full py-[0.6rem] px-[1rem] border-solid border-[1px] border-black rounded'/>

      <input type='password' placeholder='Enter Password' className='w-full py-[0.6rem] px-[1rem] border-solid border-[1px] border-black rounded'/>

      <button className='px-[1.5rem] py-[0.4rem] bg-[#FF5252] rounded mt-[1rem] text-white'>Login</button>
      </div>
    </div>
  )
}

export default Login;
