import { useElements, useStripe } from '@stripe/react-stripe-js';
import axios from 'axios';
import React, { useEffect, useState } from 'react';
import {useSearchParams} from "react-router-dom";
import { useNavigate } from 'react-router-dom';


function Payment() {
    let elements = useElements();
    let stripe = useStripe();
    const [card,setCard] = useState();

    const [searchParams] = useSearchParams();
    const price = searchParams.get("price");
    const interval = searchParams.get("interval");
    const plan = searchParams.get("plan");


    let auth = localStorage.getItem("user");
    let user = JSON.parse(auth);

    const navigate = useNavigate();


    useEffect(() => {
        if (elements && !card) {
            const cardElement = elements.create('card');
            setCard(cardElement);
            if (cardElement) {
                cardElement.mount('.purchasecard');
            }
        }
    }, [elements, card]);


    const purchaseNow = async()=>{
        try {
            const { paymentMethod } = await stripe.createPaymentMethod({
                type: 'card',
                card: card,
            });

            console.log("payment", paymentMethod);

            const response = await axios.post("http://localhost:5000/", {
                id: paymentMethod.id,
                amount: price,
                interval: interval,
                plan: plan,
                name: user.name,
                email: user.email,
            });

            console.log("response", response);


            if(response.data){
                let formdata = new FormData();
                formdata.append("premium", true);

                let response = await axios.post(`http://localhost:5000/auth/updateUser/${user._id}`, formdata);
                console.log("response.data",response.data)
                if(response.data){
                  localStorage.setItem("user", JSON.stringify(response.data.user));
                 
                }
            }
            
            navigate("/")
        } 
        catch (error) {
            console.error("Error during purchase:", error.message);
        }
    }



  return (
    <div className='mt-[8rem]'>
      
      <div className="purchasecard">

          </div>
          <button className='rounded mt-[2rem] px-[1rem] py-[0.5rem] bg-black text-white' onClick={purchaseNow}>purchase</button>
    </div>
  )
}

export default Payment;
