import React, { useState } from "react";
import {Link} from "react-router-dom"
const Navbar = ()=>{
    const [menuBtn, setMenuBtn] = useState(false);
    return(
        <>
            <header className="fixed top-0 left-0 w-full bg-[#89C6E1] shadow-md z-100">
            <div className="flex items-center justify-between w-full gap-4 p-4 px-8">

            <div>
                <img src="./images/logo.png" alt="" className="w-[90%]"/>
            </div>

            <ul className={`flex-col md:flex-row md:items-center p-1 m-0 absolute md:relative md:flex right-[-100%] md:right-0 top-full md:top-0 bg-[#89C6E1] md:bg-transparent w-[30rem] md:w-auto transition-[right] duration-500 ease-in-out ${menuBtn ? "right-0" : ""}`}>

            <li className="mx-3 text-white"><Link to="/">Home</Link></li>
            <li className="mx-3 text-white"><Link to="/shop">Shop</Link></li>
            <li className="mx-3 text-white"><Link to="/about">About</Link></li>
            <li className="mx-3 text-white"><Link to="/contact">Contact</Link></li>
            <li className="mx-3 text-white"><Link to="/login">Login</Link></li>
            <li className="mx-3 text-white"><Link to="/signup">Signup</Link></li>
            <li><Link to="/logout">Logout</Link></li>
            </ul>

            <div id="menu-btn" onClick={()=> setMenuBtn(!menuBtn)} 
       className={`${
              menuBtn ? "fas fa-times" : "fas fa-bars"
            } text-white text-xl md:hidden`}></div>
            </div>
            </header>
        </>
    )
}

export default Navbar;
