import React, { useEffect, useState } from "react";
import axios from "axios"
import {Link} from "react-router-dom"

const Todo = ()=>{
    const [title,setTitle] = useState();
    const [content,setContent] = useState();
    const [id,setId] = useState();
    const [todos,setTodos] = useState();
    const [userId, setuserId] = useState();
    
    useEffect(()=>{
        getTodo()
    },[])

    const getTodo = async()=>{
        let auth = localStorage.getItem("user");
        let user = JSON.parse(auth);
        let response = await axios.get(`http://localhost:5000/getTodo/${user._id}`);
        setId(response.data._id);
        setTodos(response.data.todo);
        setuserId(user._id);
    }

    const handleTodo =async()=>{
       let auth = localStorage.getItem("user");
       let user = JSON.parse(auth);
       let response = await axios.get(`http://localhost:5000/getTodo/${userId}`);

       if(response.data.user){
        let data = {
            todo:{
                title:title,
                content:content
            }
        }
        let updateTodo = await axios.put(`http://localhost:5000/todo/update/${id}`,data);
        setTitle(""); setContent("")
       }
       else{
        let data = {
            user:user._id,
            classname:1,
            todo:{
                title:title,
                content:content
            }
        }
        let addTodo = await axios.post(`http://localhost:5000/todo/add`,data);
        setTitle(""); setContent("")
       }

    // update the todo
       getTodo();
    }

    return(
        <>
        <div className="card">
        <div className="search">
        <div className="inputDivs">
            <input type="text" placeholder="Write a Title.." onChange={(e)=>setTitle(e.target.value)} value={title} />
            <input type="text" placeholder="Write a Content.." onChange={(e)=>setContent(e.target.value)} value={content} />
        </div>
            <button onClick={handleTodo}>âž•</button>
        </div>
    </div>

    <div className="todos">
    {
        todos && todos.length>0 && todos.map((item)=>{
            return(
                <>
                <div className="todo">
                   <h6>{item.title}</h6>
                   <p>{item.content}</p>
                   <div className="links">
                    <Link>Update</Link>
                    <Link style={{color:"red"}}>Delete</Link>
                   </div>
                </div>
                </>
            )
        })
    }
    </div>
    </>
    )
}

export default Todo;
