import React, { Component } from "react";
let initialCount = 0;
class ClassCounter extends Component{

    constructor(){
        super();
        this.state = {
            count : initialCount
        }
    }



      
    render(){
        return(
            <>
            <h1>{this.state.count}</h1>
                <button onClick={this.setState({count : this.state.count+1})}>Increment</button>
                <button onClick={this.setState({count : this.state.count+1})}>Increment</button>
            </>
        )
    }
}

export default ClassCounter;
