import React,{useEffect, useState} from "react";
import axios from "axios";

const Todos = ()=>{
    let auth = localStorage.getItem("user");
    let userParsed = JSON.parse(auth);
    let user = userParsed._id;
    const [value,setValue] = useState();
    const [todos,setTodos] = useState();

    useEffect(()=>{
        getTodo()
    })

    const getTodo = async()=>{
        let result = await axios.get(`http://localhost:5000/getTodo/${userParsed._id}`);
        console.log("result", result)
        setTodos(result.data.todo);
    }

    console.log("todos",todos);

    const addTodo = async()=>{
        let result = await axios.get(`http://localhost:5000/getTodo/${userParsed._id}`);
        console.log("result", result)
        setTodos(result.data.todo);

        if(result.data.user){
            let id = result.data._id;
            let updateTodo = await fetch(`http://localhost:5000/todo/update/${id}`,{
                method:"put",
                body:JSON.stringify({todo:value}),
                headers:{
                    "Content-Type":"application/json",
                    authorization:`bearer ${JSON.parse(localStorage.getItem("token"))}`
                }
            });
            let data = await updateTodo.json();
            console.log(data);
            setValue("")  
        }

        else{
            let addTodo = await fetch(`http://localhost:5000/todo/add`,{
                method:"post",
                body:JSON.stringify({user,classname:1,todo:value}),
                headers:{
                    "Content-Type":"application/json",
                    authorization:`bearer ${JSON.parse(localStorage.getItem("token"))}`
                }
            });
            let data = await addTodo.json();
            console.log(data);
            setValue("")   
        }
    }

    return(
        <div className="card">
        <div className="search">
            <input type="text" placeholder="Write something here..." onChange={(e)=> setValue(e.target.value)} value={value} />
            <button onClick={addTodo} >âž•</button>
        </div>
        
        
    </div>
    )
}

export default Todos;
