import React, { useState } from "react";

const Login = ()=>{
    const [name,setName] = useState();
    const [email,setEmail] = useState();

    const handleData=async()=>{
        let data = {
            name:name,
            email:email,
            password:password
        }
        let response = await axios.post('http://localhost:5000/auth/register',data);
        localStorage.setItem("user",JSON.stringify(response.data.user));
        localStorage.setItem("token",JSON.stringify(response.data.auth));
        navigate("/")
    }

    return(
        <div className="register">
        <h1>Login</h1>
        <input type="text" placeholder="Enter name" className="inputBox" onChange={(e)=> setName(e.target.value)} value={name}/>
        <input type="text" placeholder="Enter email" className="inputBox" onChange={(e)=> setEmail(e.target.value)} value={email}/>
        <button className="appButton" onClick={handleData}>Login</button>
        </div>
    )
}

export default Login;
