import React, { useEffect, useState } from "react";

const Blog = () => {

    const [products,setProducts] = useState();

  useEffect(() => {
    getAllProducts();
  });

  const getAllProducts = async () => {
    let response = await axios.get('http://localhost:5000/posts/getProducts');
    if(response.data){
        setProducts(response.data)
    }
  };

  return (
  <>
  {
    products.map((post,index)=>{
        return(
            <>
            <div className="postDiv" id={postId} key={index.toString()}>
        <img src={post.profile} alt="" className="post-img" />
     <div style={{display:"flex",justifyContent:"space-between"}}>
      <span className="post-category">{post.category}</span>
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginTop: '0.5rem' }}>
      
    </div>
    </div>


      <h2 className="post-title">{post.title}</h2>
      <p className="post-desc">{post.description}</p>
      <div className="post-profile">
          <div className="profile-img">
              <img alt="" />
          </div>
          <div className="profile-desc">
              <h3></h3>
              <p>{post.createdAt}</p>
          </div>
      </div>
      </div> 
            </>
        )
    })
  }
  </>
  );
};

export default Blog;
