import React, { useEffect, useState } from "react";
import {useParams} from "react-router-dom";
import axios from "axios";

const UpdateTask = () => {
    const [title,setTitle] = useState();
    const [content,setContent] = useState();

    const params = useParams();

    const handleTask = async()=>{
      console.log(title,content)
    }

    useEffect(()=>{
      getSingleTodo()
    },[])

    const getSingleTodo = async()=>{
      let result = await axios.get(`http://localhost:5000/todo/singletask/${params.id}`);
      console.log("re",result);
      setTitle(result.todo.title);
  }


  return (
    <div className="register">
      <h1>Update Your Task</h1>
      <input type="text" placeholder="Enter Title" className="inputBox" onChange={(e)=>setTitle(e.target.value)} value={title} />

      <textarea  placeholder="Enter Content" className="inputBox" onChange={(e)=>setContent(e.target.value)} value={content} rows={5} cols={5}></textarea>

      <button onClick={handleTask} className="appButton">Update</button>

    </div>
  );
};

export default UpdateTask;
