import React, { useState,useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Login = ()=>{
    const [email,setEmail] = useState();
    const [password,setPassword] = useState();

    const navigate = useNavigate();

    useEffect(()=>{
        let auth = localStorage.getItem("user");
        if(auth){
            navigate("/")
        }
    })

    const handleData = async()=>{
        let result = await fetch("http://localhost:5000/auth/login",{
            method:"post",
            body:JSON.stringify({email,password}),
            headers:{
                "Content-Type":"application/json"
            }
        }) 
        result = await result.json();
        if(result.auth){
            localStorage.setItem("user",JSON.stringify(result.user));
            localStorage.setItem("token",JSON.stringify(result.auth));
            navigate("/")
        }
        else{
         alert("Please Enter Correct Details!")  
         setEmail("");
         setPassword("");
        }
       
    }

    return(
        <div className="login">
        <h2>Login</h2>

        <input type="text" placeholder="Enter email" className="inputBox" onChange={(e)=>setEmail(e.target.value)} value={email}/>

        <input type="password" placeholder="Enter password" className="inputBox" onChange={(e)=>setPassword(e.target.value)} value={password}/>

        <button className="appButton" onClick={handleData}>Login</button>
        </div>
    )
}

export default Login;
