import React, { useState } from "react";
function Login(){

    const [user, setUser] = useState("");
    const [pass,setPass] = useState("");
    const [userErr, setUserErr] = useState(false);
    const [passErr,setPassErr] = useState(false);

    function formSubmitted(e){
        e.preventDefault();
        if(user.length <3 || pass.length<3){
            alert("Invalid user id or password!");
        }
        else{
            alert("Form Submitted!")
        }
        setUser("");
            setPass("");
    }

    function userInput(e){
        let input = e.target.value;
        if(input.length <3){
            setUserErr(true);
        }
        else{
            setUserErr(false);
        }  
        setUser(input);
    }

    function passInput(e){
        let input = e.target.value;
        if(input.length <3){
            setPassErr(true);
        }
        else{
            setPassErr(false);
        }  
        setPass(input)
    }

    return(
        <>
            <h1>Login</h1> <br/>
            <form onSubmit={formSubmitted}>
                <input type="text" placeholder="Enter User Id" onChange={userInput}/> {userErr ? <span>Invalid User id!</span> : ""}
               <br/>  <br/>
                <input type="text" placeholder="Enter Password" onChange={passInput}/> {passErr ? <span>Invalid Password!</span> : ""}
                <br/> <br/>
                <button type="submit">Submit</button>

            </form>
        </>
    )
}

export default Login;
