/*
import React from "react";
import { Link, NavLink } from "react-router-dom";
function Navbar(){
    return(
        <>
        <ul className="navbar">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/about">About</Link></li>
            <li><Link to="/contact">Contact</Link></li>
            <li><a href="/">Home</a></li>
            <li><a href="/about">About</a></li>
        </ul>
        </>
    )
}
*/

// By using NavLink to give styling
// Add styling to active class
/*
import React from "react";
import { NavLink } from "react-router-dom";
function Navbar(){
    return(
        <>
        <ul className="navbar">
        <li><NavLink className="navbarLink"  style={{textDecoration: "none"}} to="/">Home</NavLink></li>
            <li><NavLink className="navbarLink" style={{textDecoration: "none"}} to="/about">About</NavLink></li>
            <li><NavLink className="navbarLink" style={{textDecoration: "none"}} to="/contact">Contact</NavLink></li>
        </ul>
        </>
    )
}
*/


// Add styling by using style
import React from "react";
import { NavLink } from "react-router-dom";
function Navbar(){
    return(
        <>
        <ul className="navbar">
         <li><NavLink className="navbarLink" 
         style={(isActive)=>{return {color: isActive? "skyblue" : "" }}}
          to="/">Home</NavLink></li>
            <li><NavLink className="navbarLink"  style={(isActive)=>{return {color: isActive? "skyblue" : "" }}} to="/about">About</NavLink></li>
            <li><NavLink className="navbarLink" to="/contact">Contact</NavLink></li>
        </ul>
        </>
    )
}
export default Navbar
