import React,{useState} from "react";

const Todos = ()=>{
    let auth = localStorage.getItem("user");
    let userParsed = JSON.parse(auth);
    let user = userParsed._id;
    const [value,setValue] = useState();

    const addTodo = async()=>{
        let result = await fetch(`http://localhost:5000/getTodo/${userParsed._id}`);
        if(result.user){
            result = await result.json();
            console.log(result);
        }
        else{
            console.log("Not current user");
            let addTodo = await fetch(`http://localhost:5000/todo/add`,{
                method:"post",
                body:JSON.stringify({user,className:1,value}),
                headers:{
                    "Content-Type":"application/json",
                    
                }
            });
            let data = await addTodo.json();
            console.log(data);
        }
    }

    return(
        <div className="card">
        <div className="search">
            <input type="text" placeholder="Write something here..." onChange={(e)=> setValue(e.target.value)} value={value} />
            <button onClick={addTodo} >âž•</button>
        </div>
        
        
    </div>
    )
}

export default Todos;
