import React, { useEffect, useState } from "react";
import {useNavigate} from "react-router-dom"
import axios from "axios"

const Signup = ()=>{
    const [name,setName] = useState();
    const [email,setEmail] = useState();
    const [password,setPassword] = useState();
    const [age,setAge] = useState();
    const [gender,setGender] = useState();
    const [file,setFile] = useState();

    const [image,setImage] = useState();
    
    const navigate = useNavigate();


    useEffect(()=>{
        let auth = localStorage.getItem("user");
        if(auth){
            navigate("/")
        }
    })

  
    const handleData = async()=>{
        const formdata = new FormData();
        formdata.append("name",name)
        formdata.append("email",email)
        formdata.append("password",password)
        formdata.append("age",age)
        formdata.append("gender",gender)
        formdata.append("file",file)

    
        let response = await axios.post("http://localhost:5000/auth/register",formdata);
        
        localStorage.setItem("user",JSON.stringify(response.data.result));
        localStorage.setItem("token",JSON.stringify(response.data.auth));
        navigate("/")
    }

    useEffect(()=>{
        getImage();
    },[])

    const getImage = async()=>{
        let response = await axios.get("http://localhost:5000/getImage");
        console.log("response", response.data)
        setImage(response.data[0].image)
    }

    return(
        <div className="register">
        <h2>Register</h2>
        <input type="text" placeholder="Enter name" className="inputBox" onChange={(e)=>setName(e.target.value)} value={name}/>

        <input type="text" placeholder="Enter email" className="inputBox" onChange={(e)=>setEmail(e.target.value)} value={email}/>

        <input type="password" placeholder="Enter password" className="inputBox" onChange={(e)=>setPassword(e.target.value)} value={password}/>

        <input type="number" placeholder="Enter age" className="inputBox" onChange={(e)=>setAge(e.target.value)} value={age}/>

        <input type="text" placeholder="Enter gender" className="inputBox" onChange={(e)=>setGender(e.target.value)} value={gender}/>

        <input type="file" name='file' onChange={(e)=> setFile(e.target.files[0]) }/>

        <button className="appButton" onClick={handleData}>Signup</button>

        <img src={`http://localhost:5000/Images/${image}`} alt="image"/>

        </div>
        
    )
}

export default Signup;
