import axios from "axios";
import React, { useEffect, useState } from "react";
import {Link,useNavigate} from "react-router-dom";

const Blog = () => {
  const [posts, setPosts] = useState();
  const [popup, setPopup] = useState({});
  let auth = localStorage.getItem("user");
  let userid = JSON.parse(auth)._id;

  const navigate = useNavigate();

  useEffect(() => {
    getAllProducts();
  }, []);

  const handle401 = (status)=>{
    alert(status)
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    navigate("/login")
  }

  const getAllProducts = async () => {
    const headers = {
      "Authorization": `bearer ${JSON.parse(localStorage.getItem("token"))}`,
      "Content-Type":"application/json"
  }

    let response = await axios.get("http://localhost:5000/posts/getPosts",{headers});
    if (response.data) {
        setPosts(response.data);
    }

    console.log(response.status)
    console.log(typeof(response.status))

    if(response.status === 401){
      console.log(response.status)
      handle401(response.status);
    }
    else{
      
    }

  };

  const togglePopup = (postId) => {
    setPopup((prevpopup) => {
      return {
        ...prevpopup,
        [postId]: !prevpopup[postId],
      };
    });
  };

  const deletePost = async(postid)=>{
    const headers = {
      "Authorization": `bearer ${JSON.parse(localStorage.getItem("token"))}`,
      "Content-Type":"application/json"
  }

    let response = await axios.delete(`http://localhost:5000/posts/delete/${postid}`,{headers});
    console.log(response);
    getAllProducts();
  }


  const formatDate = (dateString) => {
    const d = new Date(dateString);
    let month = '' + (d.getMonth() + 1);
    let day = '' + d.getDate();
    const year = d.getFullYear();

    if (month.length < 2) month = '0' + month;
    if (day.length < 2) day = '0' + day;

    return [day, month, year].join('-');
  };


  return (
    <div className="posts">
      {posts &&
        posts.map((post, index) => {
          let postid = post._id;
          let post_userid = post.userId._id;

          return (
            <>
              <div className="postDiv" key={index.toString()}>
               <Link to={`/${post._id}`}>
               <img
                  src={`http://localhost:5000/Images/${post.profile}`}
                  alt="" className="post-img"/>
                </Link>
                <div className="space-between">
                  <span className="post-category">{post.category}</span>
                  <div className="center">
                    <div className="dots">
                      {post_userid === userid ? (
                        <svg
                          onClick={() => togglePopup(postid)}
                          fill="#000000"
                          height="20px"
                          width="50px"
                          version="1.1"
                          xmlns="http://www.w3.org/2000/svg"
                          xmlnsXlink="http://www.w3.org/1999/xlink"
                          viewBox="0 0 32.055 32.055"
                          xmlSpace="preserve"
                        >
                          <g>
                            <path d="M3.968,12.061C1.775,12.061,0,13.835,0,16.027c0,2.192,1.773,3.967,3.968,3.967c2.189,0,3.966-1.772,3.966-3.967 C7.934,13.835,6.157,12.061,3.968,12.061z M16.233,12.061c-2.188,0-3.968,1.773-3.968,3.965c0,2.192,1.778,3.967,3.968,3.967 s3.97-1.772,3.97-3.967C20.201,13.835,18.423,12.061,16.233,12.061z M28.09,12.061c-2.192,0-3.969,1.774-3.969,3.967 c0,2.19,1.774,3.965,3.969,3.965c2.188,0,3.965-1.772,3.965-3.965S30.278,12.061,28.09,12.061z"></path>
                          </g>
                        </svg>
                      ) : (
                        ""
                      )}
                      {popup[postid] ? (
                        <div className="dotsPopup">
                          <button><Link to={`/update/${postid}`}>Edit</Link></button>
                          <button onClick={()=>deletePost(postid)}>Delete</button>
                        </div>
                      ) : (
                        ""
                      )}
                    </div>
                  </div>
                </div>

                <h2 className="post-title">{post.title}</h2>
                <p className="post-desc">{post.body}</p>
                <div className="post-profile">
                  <div className="profile-img">
                    <img
                      alt=""
                      src={`http://localhost:5000/Images/${post.userId.file}`}
                    />
                  </div>
                  <div className="profile-desc">
                    <h3>{post.userId.name}</h3>
                    <p>{formatDate(post.createdAt)}</p>
                  </div>
                </div>
              </div>
            </>
          );
        })}
    </div>
  );
};

export default Blog;
