import React,{useState} from "react";
import {Link,useNavigate} from "react-router-dom"

const Navbar = ()=>{
    let auth = localStorage.getItem("user");
    let user = JSON.parse(auth);
    const navigate = useNavigate();

    const [menuBtn,setMenuBtn] = useState(false);


    const deleteUser = ()=>{
        localStorage.clear();
        navigate("/signup")
    }

    return(
        <header className="header">
        <div className="nav">
        <div className="menuContainer">
        <div id="menu-btn" onClick={()=> setMenuBtn(!menuBtn)} 
        className={`${menuBtn ? "fas fa-times" : "fas fa-bars" }`}></div>
        
          {
            auth?
            <div className="logo">
            <img src={`http://localhost:5000/Images/${user.file}`} alt="" className="profile"/>
           <h3> {user.name}</h3>
           </div> :""
          }
        {
            auth?
            <ul className="nav-ul nav-right ">                                            
            <li><Link to="/">Home</Link></li>
            <li><Link to="/blog">Blog</Link></li>
            <li><Link to="/add">Add Post</Link></li>
            <li><Link to="/contact">Contact</Link></li>
            <li><Link to="/signup" onClick={deleteUser}>Logout</Link></li> 
        </ul>
        :
        <ul className="nav-ul nav-right">         
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/signup">Signup</Link></li>
        </ul>
        }
        
        </div>
        </div>
        </header>
    )
}

export default Navbar;
