import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const PostDetail = () => {
  const params = useParams();
  const [post,setPost] = useState();
  console.log(params.id);
  useEffect(() => {
    getPost();
  });

  const getPost = async () => {
    let post = await axios.get(
      `http://localhost:5000/posts/getPost/${params.id}`
    );
   
  };

  return (
    <>
      <section id="postDetails" className="container">
        <div className="postDetails">
          <div className="detail-desc">
            <div className="icons-div">
              <i className="fas fa-pencil-alt"></i>
              <h3></h3>
            </div>
            <div className="icons-div">
              <i className="fas fa-clock"></i>
              <p>post.createdAt</p>
            </div>
            <div className="icons-div">
              <i className="far fa-folder-open"></i>
              <span className="slider-category">category</span>
            </div>
          </div>
          <h2 className="detail-heading">post title</h2>
          <img alt="" className="detail-img" />

          <div className="likeComment"></div>

          <p className="details-des">body</p>

          <div className="likecountdiv">
            <p className="hashtags">#horizontalimage #image #post category</p>
            <div className="likecount">
              <div className="postDComment">
                <i className="fas fa-comments"></i>
                <p>comments length</p>
              </div>
              <div className="postDLike">
                <i className="fas fa-heart"></i>
                <p>like count</p>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default PostDetail;
