import React,{useEffect, useState} from "react";
import axios from "axios";

const Todos = ()=>{
    let auth = localStorage.getItem("user");
    let userParsed = JSON.parse(auth);
    let user = userParsed._id;
    const [value,setValue] = useState();
    const [todos,setTodos] = useState();
    const [id,setId] = useState();

    useEffect(()=>{
        getTodo();
    },[])

    const getTodo = async()=>{
        let result = await axios.get(`http://localhost:5000/getTodo/${userParsed._id}`);
        console.log("result", result)
        setTodos(result.data.todo);
        setId(result.data._id);
    }


    const addTodo = async()=>{
        let result = await axios.get(`http://localhost:5000/getTodo/${userParsed._id}`);

        if(result.data.user){
            let id = result.data._id;
            let updateTodo = await fetch(`http://localhost:5000/todo/update/${id}`,{
                method:"put",
                body:JSON.stringify({todo:value}),
                headers:{
                    "Content-Type":"application/json",
                    authorization:`bearer ${JSON.parse(localStorage.getItem("token"))}`
                }
            });
            let data = await updateTodo.json();
            console.log(data);
            setValue("") 
        }

        else{
            let addTodo = await fetch(`http://localhost:5000/todo/add`,{
                method:"post",
                body:JSON.stringify({user,classname:1,todo:value}),
                headers:{
                    "Content-Type":"application/json",
                    authorization:`bearer ${JSON.parse(localStorage.getItem("token"))}`
                }
            });
            let data = await addTodo.json();
            console.log(data);
            setValue("")   
        }

        // update the todos
        getTodo();
    }

    const deleteItem = ()=>{
        let result
    }

    return(
        <div className="card">
        <div className="search">
            <input type="text" placeholder="Write something here..." onChange={(e)=> setValue(e.target.value)} value={value} />
            <button onClick={addTodo} >âž•</button>
        </div>
        
        <div className="todos">
            {
                todos && todos.length> 0 &&
                todos.map((item)=>{
                    return(
                        <div className="todo-list">
                        <h3>{item}</h3>
                        <button onClick={deleteItem}>delete</button>
                        </div>
                    )
                })
            }
        </div>
    </div>
    )
}

export default Todos;
