import axios from "axios";
import React, { useEffect, useState} from "react";
import {Link, useNavigate} from "react-router-dom"

const Navbar = ()=>{
    let auth = localStorage.getItem("user");
    let user = JSON.parse(auth)

    const navigate = useNavigate();

    const [image, setImage] = useState();

    const deleteUser = ()=>{
        localStorage.clear();
        navigate("/")
    }

    // useEffect(()=>{
    //     getImage();
    //     console.log("auth",user)
    // },[])

 
    // const getImage = async()=>{
    //     let response = await axios.get(`http://localhost:5000/getImage/${user._id}`);
    //     console.log(response.data)
    //     setImage(response.data[0].file)
    // }

    return(
        <div>
        {
            auth? 
            <ul className="nav-ul">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/signup" onClick={deleteUser}>Logout ({user.name})</Link></li>
            {/* <img src={`http://localhost:5000/Images/${image}`} alt=""/> */}
            </ul> 
            : 
            <ul className="nav-ul nav-right">
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/signup">Signup</Link></li>
            </ul>
        }
            
        </div>
    )
}

export default Navbar;
