import React, { useState } from 'react';
import { useNavigate, Link} from "react-router-dom"

function Header() {
  const [isOpen, setIsOpen] = useState(false);
  
  let auth = localStorage.getItem("user");
  let user = JSON.parse(auth);

  return (
    <div className="flex flex-col h-full">
      {/* Sidebar */}
      <div className={`fixed top-0 left-0 w-64 bg-gray-800 text-white h-full transform ${isOpen ? "translate-x-0" : "-translate-x-full"} transition-transform duration-300 ease-in-out md:translate-x-0`}>
        <div className="p-4">
          <h1 className="text-xl font-bold">Sidebar</h1>
          {
  auth ? 
  <div className="flex items-center justify-center gap-[0.6rem]">
            <img src={user.profile} alt="" className="w-[45px] h-[45px] rounded-[50%]"/>
            <h5 className="capitalize m-[0] font-medium">{user.name}</h5>
          </div>
          : ""
}
          <ul>
            <li><Link>Home</Link></li>
            <li><Link>Add Recipe</Link></li>
            <li><Link>Favorites</Link></li>
            <li><Link>Logout</Link></li>
          </ul>
        </div>
      </div>
      
      {/* Header */}
      <div className="flex items-center justify-between p-4 bg-gray-900 text-white md:hidden">
        <h1 className="text-xl font-bold">Header</h1>
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="focus:outline-none"
        >
          {isOpen ? (
            <i className="fas fa-times w-6 h-6"></i>
          ) : (
            <i className="fas fa-bars w-6 h-6"></i>
          )}
        </button>
      </div>
    </div>
  );
}

export default Header;
