    let heading={
        display:"flex",
        flexDirection : "column",
        justifyContent : "center",
        alignItems : "center",
        gap:"1rem",
        marginTop:"1rem"
    }

    const [name,setName] = useState("Vinita Chawla");


    function Apple(){
        alert("Hello")
    }

    return(
        <>
        <div style={heading}>
            <h1>React props</h1>
            <Student name={name} email={"vini@gmail.com"} func={Apple} other={{address:"Ghotki", role:"Student"}}
                html= {<h1>This is HTML heading</h1>}
            />
        <button onClick={()=> setName("Vanita Chawla")}>Update name</button>
        </div>
        </>
    )
}
*/

// Props in class component (Both ways 1-change state directly  2- change with function)
/*
import React, { Component} from "react";
import ClassStudent from "./Components/ClassStudent";

class App extends Component{

    constructor(){
        super();
        this.state = {
            name:"Vanita Chawla"
        }
    }
 
    updateName(){
        this.setState({name:"Vini"})
    }

    render(){
        let heading={
            display:"flex",
            flexDirection : "column",
            justifyContent : "center",
            alignItems : "center",
            gap:"1rem",
            marginTop:"1rem"
        }
    
        
        return(
            <>
            <div style={heading}>
                <h1>React props</h1>
                <ClassStudent name={this.state.name} email="vini@gmail.com"/>
                <button onClick={()=> this.setState({name:"vinita"})}>Update name1</button>
                <button onClick={()=> this.updateName()}>Update name2</button> 
            </div>
            </>
        )
    }

}
*/

// Get Input box value by changing the input or click on btn and then get value
// One method
/*
import React, { useState } from "react";
const App = ()=>{
    const [data,setData] = useState(null);
    const [value ,setValue] = useState(null);

    function getValue(val){
        console.log(val.target.value);
        setData(val.target.value)
