const App = ()=>{
    const [data,setData] = useState(null);
    const [value ,setValue] = useState(false);

    function getValue(val){
        console.log(val.target.value);
        setData(val.target.value);
        setValue(false);
    }

    return(
        <>
            <h1>Input Box value</h1>
            {value ? <h1>{data}</h1> : null}
            <input type="text" onChange={getValue}/>
            <button onClick={()=>setValue(true)}>click me</button>

        </>
    )
}
*/

// Hide , Show and Toggle in ReactJs
/*
import React, { useState } from "react";
const App = ()=>{
    const [status, setStatus] = useState(true)
    return(
        <>
           {status ? <h1>Hello Vanita!</h1> : null} 
            <button onClick={()=> setStatus(false)}>Hide</button>
            <button onClick={()=> setStatus(true)}>Show</button>
            <button onClick={()=> setStatus(!status)}>toggle</button>
        </>
    )
}
*/

// Handle Form | checkbox | input field | select
/*
import React, { useState } from "react";

const App=()=>{
    const [name, setName] = useState("");
    const [lang, setLang] = useState("");
    const [terms, setTerms] = useState(false);
    const [status, setStatus] = useState(false)


    function formSubmitted(e){
        console.log(name,lang,terms);
        setStatus(true);
        e.preventDefault();
    }
    return(
        <>
            <h1>Form Submission</h1>
            <form onSubmit={formSubmitted}>
            <input type="text" placeholder="Enter Name" onChange={(e)=> setName(e.target.value)}/> <br/><br/>
            <select onChange={(e)=> setLang(e.target.value)}>
                <option>Select Option</option>
                <option>English</option>
                <option>Sindhi</option>
                <option>Urdu</option>
            </select><br/><br/>
            <input type="checkbox" onChange={(e)=> setTerms(e.target.checked)}/><span>Terms & conditions</span><br/><br/>
            <button type="submit">Submit</button><br/><br/>
           
    </form>

    {status ? <> 
            <h2>Output</h2>
            <p>Name: {name}</p>
            <p>Language: {lang}</p>
            </> : "" }
           
        </>
    )
}
*/

// Conditional rendering | If Condition
