import ClassCounter from "./Components/ClassCounter";

const App=()=>{
    return <ClassCounter/>
}
*/

//challange... replace the array element with other
/*
import React, { useState } from "react";

const App=()=>{
  const [array, setArray] = useState(["apple"," banana"," oranges"]);

  function replacElement(index, element){
    setArray((prevValue)=>{
        let newArray = [...prevValue]
        newArray[index] = element
        return newArray
    })
  }
  
    return <>
    <h3>{array.map((fruit, index)=> <li id={index}>{fruit}</li>)}</h3> <br/>
    <button onClick={()=> replacElement(0,"mango")}>Replace</button>
   
    </>
}
*/

// Life cycle method used in react js
// component is loaded (mounted)
// When component is updated with state and props (updated)
// when component is removed (Unmounted)
// Question is when i remove the component then after reload life cycle start again or follow continued cycle. answer is it starts again from starting

// 1) constructor life cycle method
// This constructor life cycle method is used before the render method is called which contains html also so if we want something before render method then we use this constructor life cycle just like usestate which we define before render. in class component we define state in constructor b/c constructor automatically called after class.
// We cant define api in constructor
/* 
import React, { Component } from "react";
class App extends Component{
    constructor(){
        super();
        this.state={
            name:"Vanita"
        }
    }
  render(){
    return <h1>{this.state.name}</h1>
  }
}
*/

// 2) Render, life cycle method
// Render method is called when
// i. when component is created and called at start
// ii. when states and props updated then
/*
import React from "react";
import RenderCycle from "./Components/RenderCycle";

const App = ()=>{
    return <RenderCycle email = "vini@gmail.com"/>
}
*/

// 3) componentDidMount life cycle method
// It is used when
// i. all the html is completly loaded and now this method is called
// ii. we call api in this method because api is called after loading of all the page
// iii. this method is called when any thing is completely dependent on html so jese he html load hoge ye method call hojaega
// it is called after the render method this method meaning sara component mount hojaega tab he ye method call hoga
// jab component ko show ya hide karengy tab bhi ye use ho sakta a
// ye method bs component ke load hone ke bad he chalta ha jab hum stats change karte tab render method dobara chalta ha per ye componentDidMount nahi chalta ye bas ek bar he chalta ha.tab he hum api ko es ke andr call karte hei kiyu k api srf ek bar he call ate ha after html/page reload.
/*
import React, { Component } from "react";
class App extends Component{
    constructor(){
        super();
        this.state={
            name:"Vanita"
        }
        console.log("constructor");
    }
    componentDidMount(){
        console.log("componentDidMount");
    }
    render(){
        console.log("render");
        return (
        <>
        <h1>componentDidMount</h1>
        <p>{this.state.name}</p>
        <button onClick={()=>{this.setState({name:"vini"})}}>Update</button>
        </>
        )
    }
}
*/

// 4)  componentDidUpdate life cycle method
// Ye method tab chalta ha jab stats ya props update hote hei
// when i click on button then stat changes and this function called
// es ke andr hum api call karwa sakte hei per jab conditionally call karwane ho tou otherwise nae kar sakte other componentDidMount mei karwayengy

// With name
/*
import React, { Component } from "react";
class App extends Component{
    constructor(){
        super();
        this.state={
            name:"vini"
        }

        console.log("constructor")

    }

    componentDidUpdate(){
        console.log("componentDidUpdate")
    }

    render(){
        console.log("render")

        return(
            <>
                <h1>componentDidUpdate {this.state.name}</h1>
                <button onClick={()=>{this.setState({name:"chawla"})}}>Update</button>
            </>
        )
    }

}
*/

// also with counter
/*
