    const [count,setCount] = useState("vini")

    useEffect(()=>{
        console.log("useEffect")
    })
    return(
        <>
        <h1>UseEffect {count}</h1>
        <button onClick={()=>setCount("vinita")}>update</button>
        </>
    )
}
